import logging from logging.handlers import RotatingFileHandler
from rich.logging import RichHandler
logger = logging.getLogger("graphgen")
def set_logger( log_file: str, log_level: int = logging.INFO, *, if_stream: bool = True, max_bytes: int = 50 * 1024 * 1024, # 50 MB backup_count: int = 5, force: bool = False, ):
if logger.hasHandlers() and not force: return
if force: logger.handlers.clear()
logger.setLevel(log_level) logger.propagate = False
if logger.handlers: logger.handlers.clear()
if if_stream: console = RichHandler(level=log_level, show_path=False, rich_tracebacks=True) console.setFormatter(logging.Formatter("%(message)s")) logger.addHandler(console)
file_handler = RotatingFileHandler( log_file, maxBytes=max_bytes, backupCount=backup_count, encoding="utf-8", ) file_handler.setLevel(log_level) file_handler.setFormatter( logging.Formatter( "[%(asctime)s] %(levelname)s [%(name)s:%(filename)s:%(lineno)d] %(message)s", datefmt="%y-%m-%d %H:%M:%S", ) ) logger.addHandler(file_handler)
def parse_log(log_file: str): with open(log_file, "r", encoding="utf-8") as f: lines = f.readlines() return lines

import html
import json
import os
import re
from typing import Any

from .log import logger


def pack_history_conversations(*args: str):
    roles = ["user", "assistant"]
    return [
        {"role": roles[i % 2], "content": content} for i, content in enumerate(args)
    ]


def split_string_by_multi_markers(content: str, markers: list[str]) -> list[str]:
    """Split a string by multiple markers"""
    if not markers:
        return [content]
    results = re.split("|".join(re.escape(marker) for marker in markers), content)
    return [r.strip() for r in results if r.strip()]


# Refer the utils functions of the official GraphRAG implementation:
# https://github.com/microsoft/graphrag
def clean_str(input: Any) -> str:
    """Clean an input string by removing HTML escapes, control characters, and other unwanted characters."""
    # If we get non-string input, just give it back
    if not isinstance(input, str):
        return input

    result = html.unescape(input.strip())
    # https://stackoverflow.com/questions/4324790/removing-control-characters-from-a-string-in-python
    return re.sub(r"[\x00-\x1f\x7f-\x9f]", "", result)


async def handle_single_entity_extraction(
    record_attributes: list[str],
    chunk_key: str,
):
    if len(record_attributes) < 4 or record_attributes[0] != '"entity"':
        return None
    # add this record as a node in the G
    entity_name = clean_str(record_attributes[1].upper())
    if not entity_name.strip():
        return None
    entity_type = clean_str(record_attributes[2].upper())
    entity_description = clean_str(record_attributes[3])
    entity_source_id = chunk_key
    return {
        "entity_name": entity_name,
        "entity_type": entity_type,
        "description": entity_description,
        "source_id": entity_source_id,
    }


def is_float_regex(value):
    return bool(re.match(r"^[-+]?[0-9]*\.?[0-9]+$", value))


async def handle_single_relationship_extraction(
    record_attributes: list[str],
    chunk_key: str,
):
    if len(record_attributes) < 4 or record_attributes[0] != '"relationship"':
        return None
    # add this record as edge
    source = clean_str(record_attributes[1].upper())
    target = clean_str(record_attributes[2].upper())
    edge_description = clean_str(record_attributes[3])

    edge_source_id = chunk_key
    return {
        "src_id": source,
        "tgt_id": target,
        "description": edge_description,
        "source_id": edge_source_id,
    }


def load_json(file_name):
    if not os.path.exists(file_name):
        return None
    with open(file_name, encoding="utf-8") as f:
        return json.load(f)


def write_json(json_obj, file_name):
    if not os.path.exists(os.path.dirname(file_name)):
        os.makedirs(os.path.dirname(file_name), exist_ok=True)
    with open(file_name, "w", encoding="utf-8") as f:
        json.dump(json_obj, f, indent=4, ensure_ascii=False)


def format_generation_results(
    results: dict[str, Any], output_data_format: str
) -> list[dict[str, Any]]:
    if output_data_format == "Alpaca":
        logger.info("Output data format: Alpaca")
        results = [
            {
                "instruction": item["question"],
                "input": "",
                "output": item["answer"],
            }
            for item in list(results.values())
        ]
    elif output_data_format == "Sharegpt":
        logger.info("Output data format: Sharegpt")
        results = [
            {
                "conversations": [
                    {"from": "human", "value": item["question"]},
                    {"from": "gpt", "value": item["answer"]},
                ]
            }
            for item in list(results.values())
        ]
    elif output_data_format == "ChatML":
        logger.info("Output data format: ChatML")
        results = [
            {
                "messages": [
                    {"role": "user", "content": item["question"]},
                    {"role": "assistant", "content": item["answer"]},
                ]
            }
            for item in list(results.values())
        ]
    else:
        raise ValueError(f"Unknown output data format: {output_data_format}")
    return results

from hashlib import md5

def compute_args_hash(*args):
    return md5(str(args).encode()).hexdigest()

def compute_content_hash(content, prefix: str = ""):
    return prefix + md5(content.encode()).hexdigest()

from functools import wraps
from typing import Any, Callable

from .loop import create_event_loop


def async_to_sync_method(func: Callable) -> Callable:
    @wraps(func)
    def wrapper(self, *args, **kwargs) -> Any:
        loop = create_event_loop()
        return loop.run_until_complete(func(self, *args, **kwargs))

    return wrapper

from typing import Any, Dict, List

import pandas as pd

from graphgen.bases.base_reader import BaseReader


class CsvReader(BaseReader):
    def read(self, file_path: str) -> List[Dict[str, Any]]:

        df = pd.read_csv(file_path)
        if self.text_column not in df.columns:
            raise ValueError(f"Missing '{self.text_column}' column in CSV file.")
        return df.to_dict(orient="records")

import json
from typing import Any, Dict, List

from graphgen.bases.base_reader import BaseReader
from graphgen.utils import logger


class JsonlReader(BaseReader):
    def read(self, file_path: str) -> List[Dict[str, Any]]:
        docs = []
        with open(file_path, "r", encoding="utf-8") as f:
            for line in f:
                try:
                    doc = json.loads(line)
                    if self.text_column in doc:
                        docs.append(doc)
                    else:
                        raise ValueError(
                            f"Missing '{self.text_column}' in document: {doc}"
                        )
                except json.JSONDecodeError as e:
                    logger.error("Error decoding JSON line: %s. Error: %s", line, e)
        return docs

from typing import Any, Dict, List

from graphgen.bases.base_reader import BaseReader


class TxtReader(BaseReader):
    def read(self, file_path: str) -> List[Dict[str, Any]]:
        docs = []
        with open(file_path, "r", encoding="utf-8") as f:
            for line in f:
                line = line.strip()
                if line:
                    docs.append({self.text_column: line})
        return docs

import json
from typing import Any, Dict, List

from graphgen.bases.base_reader import BaseReader


class JsonReader(BaseReader):
    def read(self, file_path: str) -> List[Dict[str, Any]]:
        with open(file_path, "r", encoding="utf-8") as f:
            data = json.load(f)
            if isinstance(data, list):
                for doc in data:
                    if self.text_column not in doc:
                        raise ValueError(
                            f"Missing '{self.text_column}' in document: {doc}"
                        )
                return data
            raise ValueError("JSON file must contain a list of documents.")

import time
from datetime import datetime, timedelta
import asyncio

from graphgen.utils import logger


class RPM:

    def __init__(self, rpm: int = 1000):
        self.rpm = rpm
        self.record = {'rpm_slot': self.get_minute_slot(), 'counter': 0}

    def get_minute_slot(self):
        current_time = time.time()
        dt_object = datetime.fromtimestamp(current_time)
        total_minutes_since_midnight = dt_object.hour * 60 + dt_object.minute
        return total_minutes_since_midnight

    async def wait(self, silent=False):
        current = time.time()
        dt_object = datetime.fromtimestamp(current)
        minute_slot = self.get_minute_slot()

        if self.record['rpm_slot'] == minute_slot:
            # check RPM exceed
            if self.record['counter'] >= self.rpm:
                # wait until next minute
                next_minute = dt_object.replace(
                    second=0, microsecond=0) + timedelta(minutes=1)
                _next = next_minute.timestamp()
                sleep_time = abs(_next - current)
                if not silent:
                    logger.info('RPM sleep %s', sleep_time)
                await asyncio.sleep(sleep_time)

                self.record = {
                    'rpm_slot': self.get_minute_slot(),
                    'counter': 0
                }
        else:
            self.record = {'rpm_slot': self.get_minute_slot(), 'counter': 0}
        self.record['counter'] += 1

        if not silent:
            logger.debug(self.record)


class TPM:

    def __init__(self, tpm: int = 20000):
        self.tpm = tpm
        self.record = {'tpm_slot': self.get_minute_slot(), 'counter': 0}

    def get_minute_slot(self):
        current_time = time.time()
        dt_object = datetime.fromtimestamp(current_time)
        total_minutes_since_midnight = dt_object.hour * 60 + dt_object.minute
        return total_minutes_since_midnight

    async def wait(self, token_count, silent=False):
        current = time.time()
        dt_object = datetime.fromtimestamp(current)
        minute_slot = self.get_minute_slot()

        # get next slot, skip
        if self.record['tpm_slot'] != minute_slot:
            self.record = {'tpm_slot': minute_slot, 'counter': token_count}
            return

        # check RPM exceed
        self.record['counter'] += token_count
        if self.record['counter'] > self.tpm:
            # wait until next minute
            next_minute = dt_object.replace(
                second=0, microsecond=0) + timedelta(minutes=1)
            _next = next_minute.timestamp()
            sleep_time = abs(_next - current)
            logger.info('TPM sleep %s', sleep_time)
            await asyncio.sleep(sleep_time)

            self.record = {
                'tpm_slot': self.get_minute_slot(),
                'counter': token_count
            }

        if not silent:
            logger.debug(self.record)

