import html
import os
from dataclasses import dataclass
from typing import Any, Optional, Union, cast

import networkx as nx

from graphgen.bases.base_storage import BaseGraphStorage
from graphgen.utils import logger


@dataclass
class NetworkXStorage(BaseGraphStorage):
    @staticmethod
    def load_nx_graph(file_name) -> Optional[nx.Graph]:
        if os.path.exists(file_name):
            return nx.read_graphml(file_name)
        return None

    @staticmethod
    def write_nx_graph(graph: nx.Graph, file_name):
        logger.info(
            "Writing graph with %d nodes, %d edges",
            graph.number_of_nodes(),
            graph.number_of_edges(),
        )
        nx.write_graphml(graph, file_name)

    @staticmethod
    def stable_largest_connected_component(graph: nx.Graph) -> nx.Graph:
        """Refer to https://github.com/microsoft/graphrag/index/graph/utils/stable_lcc.py
        Return the largest connected component of the graph, with nodes and edges sorted in a stable way.
        """
        from graspologic.utils import largest_connected_component

        graph = graph.copy()
        graph = cast(nx.Graph, largest_connected_component(graph))
        node_mapping = {
            node: html.unescape(node.upper().strip()) for node in graph.nodes()
        }  # type: ignore
        graph = nx.relabel_nodes(graph, node_mapping)
        return NetworkXStorage._stabilize_graph(graph)

    @staticmethod
    def _stabilize_graph(graph: nx.Graph) -> nx.Graph:
        """Refer to https://github.com/microsoft/graphrag/index/graph/utils/stable_lcc.py
        Ensure an undirected graph with the same relationships will always be read the same way.
        通过对节点和边进行排序来实现
        """
        fixed_graph = nx.DiGraph() if graph.is_directed() else nx.Graph()

        sorted_nodes = graph.nodes(data=True)
        sorted_nodes = sorted(sorted_nodes, key=lambda x: x[0])

        fixed_graph.add_nodes_from(sorted_nodes)
        edges = list(graph.edges(data=True))

        if not graph.is_directed():

            def _sort_source_target(edge):
                source, target, edge_data = edge
                if source > target:
                    source, target = target, source
                return source, target, edge_data

            edges = [_sort_source_target(edge) for edge in edges]

        def _get_edge_key(source: Any, target: Any) -> str:
            return f"{source} -> {target}"

        edges = sorted(edges, key=lambda x: _get_edge_key(x[0], x[1]))

        fixed_graph.add_edges_from(edges)
        return fixed_graph

    def __post_init__(self):
        """
        如果图文件存在，则加载图文件，否则创建一个新图
        """
        self._graphml_xml_file = os.path.join(
            self.working_dir, f"{self.namespace}.graphml"
        )
        preloaded_graph = NetworkXStorage.load_nx_graph(self._graphml_xml_file)
        if preloaded_graph is not None:
            logger.info(
                "Loaded graph from %s with %d nodes, %d edges",
                self._graphml_xml_file,
                preloaded_graph.number_of_nodes(),
                preloaded_graph.number_of_edges(),
            )
        self._graph = preloaded_graph or nx.Graph()

    async def index_done_callback(self):
        NetworkXStorage.write_nx_graph(self._graph, self._graphml_xml_file)

    async def has_node(self, node_id: str) -> bool:
        return self._graph.has_node(node_id)

    async def has_edge(self, source_node_id: str, target_node_id: str) -> bool:
        return self._graph.has_edge(source_node_id, target_node_id)

    async def get_node(self, node_id: str) -> Union[dict, None]:
        return self._graph.nodes.get(node_id)

    async def get_all_nodes(self) -> Union[list[dict], None]:
        return self._graph.nodes(data=True)

    async def node_degree(self, node_id: str) -> int:
        return self._graph.degree(node_id)

    async def edge_degree(self, src_id: str, tgt_id: str) -> int:
        return self._graph.degree(src_id) + self._graph.degree(tgt_id)

    async def get_edge(
        self, source_node_id: str, target_node_id: str
    ) -> Union[dict, None]:
        return self._graph.edges.get((source_node_id, target_node_id))

    async def get_all_edges(self) -> Union[list[dict], None]:
        return self._graph.edges(data=True)

    async def get_node_edges(
        self, source_node_id: str
    ) -> Union[list[tuple[str, str]], None]:
        if self._graph.has_node(source_node_id):
            return list(self._graph.edges(source_node_id, data=True))
        return None

    async def get_graph(self) -> nx.Graph:
        return self._graph

    async def upsert_node(self, node_id: str, node_data: dict[str, str]):
        self._graph.add_node(node_id, **node_data)

    async def update_node(self, node_id: str, node_data: dict[str, str]):
        if self._graph.has_node(node_id):
            self._graph.nodes[node_id].update(node_data)
        else:
            logger.warning("Node %s not found in the graph for update.", node_id)

    async def upsert_edge(
        self, source_node_id: str, target_node_id: str, edge_data: dict[str, str]
    ):
        self._graph.add_edge(source_node_id, target_node_id, **edge_data)

    async def update_edge(
        self, source_node_id: str, target_node_id: str, edge_data: dict[str, str]
    ):
        if self._graph.has_edge(source_node_id, target_node_id):
            self._graph.edges[(source_node_id, target_node_id)].update(edge_data)
        else:
            logger.warning(
                "Edge %s -> %s not found in the graph for update.",
                source_node_id,
                target_node_id,
            )

    async def delete_node(self, node_id: str):
        """
        Delete a node from the graph based on the specified node_id.

        :param node_id: The node_id to delete
        """
        if self._graph.has_node(node_id):
            self._graph.remove_node(node_id)
            logger.info("Node %s deleted from the graph.", node_id)
        else:
            logger.warning("Node %s not found in the graph for deletion.", node_id)

    async def clear(self):
        """
        Clear the graph by removing all nodes and edges.
        """
        self._graph.clear()
        logger.info("Graph %s cleared.", self.namespace)

import os
from dataclasses import dataclass

from graphgen.bases.base_storage import BaseKVStorage, BaseListStorage
from graphgen.utils import load_json, logger, write_json


@dataclass
class JsonKVStorage(BaseKVStorage):
    _data: dict[str, str] = None

    def __post_init__(self):
        self._file_name = os.path.join(self.working_dir, f"{self.namespace}.json")
        self._data = load_json(self._file_name) or {}
        logger.info("Load KV %s with %d data", self.namespace, len(self._data))

    @property
    def data(self):
        return self._data

    async def all_keys(self) -> list[str]:
        return list(self._data.keys())

    async def index_done_callback(self):
        write_json(self._data, self._file_name)

    async def get_by_id(self, id):
        return self._data.get(id, None)

    async def get_by_ids(self, ids, fields=None) -> list:
        if fields is None:
            return [self._data.get(id, None) for id in ids]
        return [
            (
                {k: v for k, v in self._data[id].items() if k in fields}
                if self._data.get(id, None)
                else None
            )
            for id in ids
        ]

    async def filter_keys(self, data: list[str]) -> set[str]:
        return {s for s in data if s not in self._data}

    async def upsert(self, data: dict):
        left_data = {k: v for k, v in data.items() if k not in self._data}
        self._data.update(left_data)
        return left_data

    async def drop(self):
        self._data = {}


@dataclass
class JsonListStorage(BaseListStorage):
    _data: list = None

    def __post_init__(self):
        self._file_name = os.path.join(self.working_dir, f"{self.namespace}.json")
        self._data = load_json(self._file_name) or []
        logger.info("Load List %s with %d data", self.namespace, len(self._data))

    @property
    def data(self):
        return self._data

    async def all_items(self) -> list:
        return self._data

    async def index_done_callback(self):
        write_json(self._data, self._file_name)

    async def get_by_index(self, index: int):
        if index < 0 or index >= len(self._data):
            return None
        return self._data[index]

    async def append(self, data):
        self._data.append(data)

    async def upsert(self, data: list):
        left_data = [d for d in data if d not in self._data]
        self._data.extend(left_data)
        return left_data

    async def drop(self):
        self._data = []

from dataclasses import dataclass
from typing import List, Union

import wikipedia
from wikipedia import set_lang

from graphgen.utils import detect_main_language, logger


@dataclass
class WikiSearch:
    @staticmethod
    def set_language(language: str):
        assert language in ["en", "zh"], "Only support English and Chinese"
        set_lang(language)

    async def search(self, query: str, num_results: int = 1) -> Union[List[str], None]:
        self.set_language(detect_main_language(query))
        return wikipedia.search(query, results=num_results, suggestion=False)

    async def summary(self, query: str) -> Union[str, None]:
        self.set_language(detect_main_language(query))
        try:
            result = wikipedia.summary(query, auto_suggest=False, redirect=False)
        except wikipedia.exceptions.DisambiguationError as e:
            logger.error("DisambiguationError: %s", e)
            result = None
        return result

    async def page(self, query: str) -> Union[str, None]:
        self.set_language(detect_main_language(query))
        try:
            result = wikipedia.page(query, auto_suggest=False, redirect=False).content
        except wikipedia.exceptions.DisambiguationError as e:
            logger.error("DisambiguationError: %s", e)
            result = None
        return result

from dataclasses import dataclass
import requests from fastapi import HTTPException
from graphgen.utils import logger
UNIPROT_BASE = "https://rest.uniprot.org/uniprotkb/search"
@dataclass class UniProtSearch: """ UniProt Search client to search with UniProt. 1) Get the protein by accession number. 2) Search with keywords or protein names. """
def get_entry(self, accession: str) -> dict: """ Get the UniProt entry by accession number(e.g., P04637). """ url = f"{UNIPROT_BASE}/{accession}.json" return self._safe_get(url).json()
def search( self, query: str, *, size: int = 10, cursor: str = None, fields: list[str] = None, ) -> dict: """ Search UniProt with a query string. :param query: The search query. :param size: The number of results to return. :param cursor: The cursor for pagination. :param fields: The fields to return in the response. :return: A dictionary containing the search results. """ params = { "query": query, "size": size, } if cursor: params["cursor"] = cursor if fields: params["fields"] = ",".join(fields) url = UNIPROT_BASE return self._safe_get(url, params=params).json()
@staticmethod def _safe_get(url: str, params: dict = None) -> requests.Response: r = requests.get( url, params=params, headers={"Accept": "application/json"}, timeout=10, ) if not r.ok: logger.error("Search engine error: %s", r.text) raise HTTPException(r.status_code, "Search engine error.") return r

from dataclasses import dataclass from typing import Dict
import matplotlib.pyplot as plt import networkx as nx
@dataclass class Visualizer: """ Class for visualizing graphs using NetworkX and Matplotlib. """
graph: nx.Graph = None communities: Dict[str, int] = None layout: str = "spring" max_nodes: int = 1000 node_size: int = 10 alpha: float = 0.6
def visualize(self, save_path: str = None): n = self.graph.number_of_nodes() if self.layout == "spring": k = max(0.1, 1.0 / (n**0.5)) pos = nx.spring_layout(self.graph, k=k, seed=42) else: raise ValueError(f"Unknown layout: {self.layout}")
plt.figure(figsize=(10, 10))
node_colors = [self.communities.get(node, 0) for node in self.graph.nodes()]
nx.draw_networkx_nodes( self.graph, pos, node_size=self.node_size, node_color=node_colors, cmap=plt.cm.tab20, alpha=self.alpha, ) nx.draw_networkx_edges(self.graph, pos, alpha=0.3, width=0.2) plt.axis("off")
if save_path: plt.savefig(save_path, dpi=300, bbox_inches="tight") print("Saved to", save_path) else: plt.show()

import asyncio from dataclasses import dataclass
from tqdm.asyncio import tqdm as tqdm_async
from graphgen.bases.datatypes import QAPair from graphgen.utils import create_event_loop
@dataclass class BaseEvaluator: max_concurrent: int = 100 results: list[float] = None
def evaluate(self, pairs: list[QAPair]) -> list[float]: """ Evaluate the text and return a score. """ return create_event_loop().run_until_complete(self.async_evaluate(pairs))
async def async_evaluate(self, pairs: list[QAPair]) -> list[float]: semaphore = asyncio.Semaphore(self.max_concurrent)
async def evaluate_with_semaphore(pair): async with semaphore: # 获取Semaphore return await self.evaluate_single(pair)
results = [] for result in tqdm_async( asyncio.as_completed([evaluate_with_semaphore(pair) for pair in pairs]), total=len(pairs), ): results.append(await result) return results
async def evaluate_single(self, pair: QAPair) -> float: raise NotImplementedError()
def get_average_score(self, pairs: list[QAPair]) -> float: """ Get the average score of a batch of texts. """ results = self.evaluate(pairs) self.results = results return sum(self.results) / len(pairs)
def get_min_max_score(self, pairs: list[QAPair]) -> tuple[float, float]: """ Get the min and max score of a batch of texts. """ if self.results is None: self.get_average_score(pairs) return min(self.results), max(self.results)

from dataclasses import dataclass

from graphgen.bases.datatypes import QAPair
from graphgen.models.evaluate.base_evaluator import BaseEvaluator
from graphgen.models.tokenizer import Tokenizer
from graphgen.utils import create_event_loop


@dataclass
class LengthEvaluator(BaseEvaluator):
    tokenizer_name: str = "cl100k_base"

    def __post_init__(self):
        self.tokenizer = Tokenizer(model_name=self.tokenizer_name)

    async def evaluate_single(self, pair: QAPair) -> float:
        loop = create_event_loop()
        return await loop.run_in_executor(None, self._calculate_length, pair.answer)

    def _calculate_length(self, text: str) -> float:
        tokens = self.tokenizer.encode(text)
        return len(tokens)

from dataclasses import dataclass
from tqdm import tqdm
from graphgen.bases.datatypes import QAPair
@dataclass class RewardEvaluator: """ Reward Model Evaluator. OpenAssistant/reward-model-deberta-v3-large-v2: 分数范围为[-inf, inf]，越高越好 """
reward_name: str = "OpenAssistant/reward-model-deberta-v3-large-v2" max_length: int = 2560 results: list[float] = None
def __post_init__(self): import torch
self.num_gpus = torch.cuda.device_count()
@staticmethod def process_chunk(rank, pairs, reward_name, max_length, return_dict): import torch from transformers import AutoModelForSequenceClassification, AutoTokenizer
device = f"cuda:{rank}" torch.cuda.set_device(rank)
rank_model = AutoModelForSequenceClassification.from_pretrained(reward_name) tokenizer = AutoTokenizer.from_pretrained(reward_name) rank_model.to(device) rank_model.eval()
results = [] with torch.no_grad(): for pair in tqdm(pairs): inputs = tokenizer( pair.question, pair.answer, return_tensors="pt", max_length=max_length, truncation=True, ) inputs = {k: v.to(device) for k, v in inputs.items()} score = rank_model(**inputs).logits[0].item() results.append(score)
return_dict[rank] = results
def evaluate(self, pairs: list[QAPair]) -> list[float]: import torch.multiprocessing as mp
chunk_size = len(pairs) // self.num_gpus chunks = [] for i in range(self.num_gpus): start = i * chunk_size end = start + chunk_size if i == self.num_gpus - 1: end = len(pairs) chunks.append(pairs[start:end])
# multi-process manager = mp.Manager() return_dict = manager.dict() processes = []
for rank, chunk in enumerate(chunks): p = mp.Process( target=self.process_chunk, args=(rank, chunk, self.reward_name, self.max_length, return_dict), ) p.start() processes.append(p)
for p in processes: p.join()
# 合并结果 results = [] for rank in range(len(chunks)): results.extend(return_dict[rank])
for p in processes: if p.is_alive(): p.terminate() p.join()
return results
def get_average_score(self, pairs: list[QAPair]) -> float: """ Get the average score of a batch of texts. """ results = self.evaluate(pairs) self.results = results return sum(self.results) / len(pairs)
def get_min_max_score(self, pairs: list[QAPair]) -> tuple[float, float]: """ Get the min and max score of a batch of texts. """ if self.results is None: self.get_average_score(pairs) return min(self.results), max(self.results)

from dataclasses import dataclass, field from typing import Set
from graphgen.bases.datatypes import QAPair from graphgen.models.evaluate.base_evaluator import BaseEvaluator from graphgen.utils import NLTKHelper, create_event_loop, detect_main_language
nltk_helper = NLTKHelper()
@dataclass class MTLDEvaluator(BaseEvaluator): """ 衡量文本词汇多样性的指标 """
stopwords_en: Set[str] = field( default_factory=lambda: set(nltk_helper.get_stopwords("english")) ) stopwords_zh: Set[str] = field( default_factory=lambda: set(nltk_helper.get_stopwords("chinese")) )
async def evaluate_single(self, pair: QAPair) -> float: loop = create_event_loop() return await loop.run_in_executor(None, self._calculate_mtld_score, pair.answer)
def _calculate_mtld_score(self, text: str, threshold=0.72) -> float: """ 计算MTLD (向前和向后的平均值)
min is 1.0 higher is better """ if not text or not text.strip(): return 0.0
lang = detect_main_language(text) tokens = nltk_helper.word_tokenize(text, lang)
stopwords = self.stopwords_zh if lang == "zh" else self.stopwords_en filtered_tokens = [word for word in tokens if word not in stopwords] filtered_tokens = [word for word in filtered_tokens if word.isalnum()]
if not filtered_tokens: return 0
# 计算向前的MTLD forward_factors = self._compute_factors(filtered_tokens, threshold)
# 计算向后的MTLD backward_factors = self._compute_factors(filtered_tokens[::-1], threshold)
# 取平均值 return (forward_factors + backward_factors) / 2
@staticmethod def _compute_factors(tokens: list, threshold: float) -> float: factors = 0 current_segment = [] unique_words = set()
for token in tokens: current_segment.append(token) unique_words.add(token) ttr = len(unique_words) / len(current_segment)
if ttr <= threshold: factors += 1 current_segment = [] unique_words = set()
# 处理最后一个不完整片段 if current_segment: ttr = len(unique_words) / len(current_segment) if ttr <= threshold: factors += 1 else: factors += 1 - (ttr - threshold) / (1 - threshold)
return len(tokens) / factors if factors > 0 else len(tokens)

from collections import defaultdict
from dataclasses import dataclass
from typing import Any, Dict, List

from graphgen.models.storage.networkx_storage import NetworkXStorage


@dataclass
class CommunityDetector:
    """Class for community detection algorithms."""

    graph_storage: NetworkXStorage = None
    method: str = "leiden"
    method_params: Dict[str, Any] = None

    async def detect_communities(self) -> Dict[str, int]:
        if self.method == "leiden":
            return await self._leiden_communities(**self.method_params or {})
        raise ValueError(f"Unknown community detection method: {self.method}")

    async def get_graph(self):
        return await self.graph_storage.get_graph()

    async def _leiden_communities(
        self, max_size: int = None, **kwargs
    ) -> Dict[str, int]:
        """
        Detect communities using the Leiden algorithm.
        If max_size is given, any community larger than max_size will be split
        into smaller sub-communities each having at most max_size nodes.
        """
        import igraph as ig
        import networkx as nx
        from leidenalg import ModularityVertexPartition, find_partition

        graph = await self.get_graph()
        graph.remove_nodes_from(list(nx.isolates(graph)))

        ig_graph = ig.Graph.TupleList(graph.edges(), directed=False)

        random_seed = kwargs.get("random_seed", 42)
        use_lcc = kwargs.get("use_lcc", False)

        communities: Dict[str, int] = {}
        if use_lcc:
            lcc = ig_graph.components().giant()
            partition = find_partition(lcc, ModularityVertexPartition, seed=random_seed)
            for part, cluster in enumerate(partition):
                for v in cluster:
                    communities[lcc.vs[v]["name"]] = part
        else:
            offset = 0
            for component in ig_graph.components():
                subgraph = ig_graph.induced_subgraph(component)
                partition = find_partition(
                    subgraph, ModularityVertexPartition, seed=random_seed
                )
                for part, cluster in enumerate(partition):
                    for v in cluster:
                        original_node = subgraph.vs[v]["name"]
                        communities[original_node] = part + offset
                offset += len(partition)

        # split large communities if max_size is specified
        if max_size is None or max_size <= 0:
            return communities

        return await self._split_communities(communities, max_size)

    @staticmethod
    async def _split_communities(
        communities: Dict[str, int], max_size: int
    ) -> Dict[str, int]:
        """
        Split communities larger than max_size into smaller sub-communities.
        """
        cid2nodes: Dict[int, List[str]] = defaultdict(list)
        for node, cid in communities.items():
            cid2nodes[cid].append(node)

        new_communities: Dict[str, int] = {}
        new_cid = 0
        for cid, nodes in cid2nodes.items():
            if len(nodes) <= max_size:
                for n in nodes:
                    new_communities[n] = new_cid
                new_cid += 1
            else:
                for start in range(0, len(nodes), max_size):
                    sub = nodes[start : start + max_size]
                    for n in sub:
                        new_communities[n] = new_cid
                    new_cid += 1

        return new_communities

